import * as Tone from 'tone'
import React, { Component } from 'react'

import * as bassSettings from './tunes/bass.js'
import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'



let synth
let chorus
let pingPongDelay

export default class Container extends Component {
  constructor(props) {
    super(props)


    this.state = {
      bassSettings
    }
  }

  handleStart = () => {
    const {bassSettings} = this.state
    synth = new Tone.Synth(bassSettings.synth)
    chorus = new Tone.Chorus(bassSettings.chorus).start()
    pingPongDelay = new Tone.PingPongDelay(bassSettings.pingPongDelay).toDestination()

    synth.chain(chorus, pingPongDelay)



  const part = new Tone.Part((time, note) => {
    synth.triggerAttackRelease(
      note.noteName,
      note.duration,
      time,
      note.velocity
    )
  }, bassSettings.sequence.steps).start(0)

  part.loopEnd = bassSettings.sequence.duration
  part.loop = true
  Tone.Transport.start()

  }

handleValueChange = (property, value) => {
  const {bassSettings} = this.state

  if (property === 'synthType') {
    synth.oscillator.type = value
    bassSettings.synth.oscillator.type = value
  } else if (property === 'pingPongDelayWet') {
    pingPongDelay.wet.value = value
    bassSettings.pingPongDelay.wet = value
  } else if (property === 'chorusWet') {
    chorus.wet.value = value
    bassSettings.chorus.wet = value
  }

  this.setState ({
    bassSettings
  })

}


  render() {
    const {bassSettings} = this.state
    const options = ['sine', 'square', 'sawtooth', 'triangle']

    return (
      <div className="Container">
        <SC_Button
          text="Art Design & Coding Community"
          handleClick={this.handleStart}
        />
        <SC_ToggleButtonSet
          name="Type"
          options={options}
          value={bassSettings.synth.oscillator.type}
          property="synthType"
          handleChange={this.handleValueChange}
        />
        <SC_Slider
          name="Delay Wet"
          min={0}
          max={1}
          step={0.01}
          value={bassSettings.pingPongDelay.wet}
          property="pingPongDelayWet"
          handleChange={this.handleValueChange}
        />
        <SC_Slider
          name="Chorus Wet"
          min={0}
          max={1}
          step={0.01}
          value={bassSettings.chorus.wet}
          property="chorusWet"
          handleChange={this.handleValueChange}
        />
      </div>
    )
  }
}
