import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'



let synth
let chorus
let pingPongDelay

export default class Container extends Component {
  constructor(props) {
    super(props)
    const synthSettings = {
      volume: 10,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'square',  // "fatsine" | "fatsquare" | "fatsawtooth" | "fattriangle" | "fatcustom" | FatTypeWithPartials | "fmsine" | "fmsquare" | "fmsawtooth" | "fmtriangle" | "fmcustom" | FMTypeWithPartials | "amsine" | "amsquare" | "amsawtooth" | "amtriangle" | "amcustom" | AMTypeWithPartials | TypeWithPartials | OscillatorType | "pulse" | "pwm"
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const pingPongDelaySettings = {
      wet: 0,
      delayTime: 0.25,
      maxDelayTime: 1
    }
    const chorusSettings = {
        wet: 0,
        type: 'sine',
        frequency: 10,
        delayTime: 10,
        depth: 0.7,
        spread: 180
    }

    this.state = {
      synthSettings,
      pingPongDelaySettings,
      chorusSettings
    }
  }

  handleStart = () => {
    const {synthSettings, pingPongDelaySettings, chorusSettings} = this.state
    synth = new Tone.Synth(synthSettings)
    chorus = new Tone.Chorus(chorusSettings).start()
    pingPongDelay = new Tone.PingPongDelay(pingPongDelaySettings).toDestination()

    synth.chain(chorus, pingPongDelay)



    // Тестовая мелодия
  const sequence = [
    // {
    //   time: '0:0:0',
    //   noteName: 'G4',
    //   duration: '4n',
    //   velocity: 1
    // },
    //
    // {
