    //   noteName: 'G4',
    //   duration: '4n',
    //   velocity: 1
    // },
    // {
    //   time: '1:1:2',
    //   noteName: 'C4',
    //   duration: '4n',
    //   velocity: 1
    // },
    // {
    //   time: '1:2:0',
    //   noteName: 'C4',
    //   duration: '4n',
    //   velocity: 1
    // },
    // {
    //   time: '1:2:2',
    //   noteName: 'C4',
    //   duration: '4n',
    //   velocity: 1
    // },
    //
    // {
    //   time: '1:3:0',
