    //   duration: '4n',
    //   velocity: 1
    // },
    // {
    //   time: '3:2:0',
    //   noteName: 'C4',
    //   duration: '4n',
    //   velocity: 1
    // }

    {
      time: '0:0:0',
      noteName: 'C4',
      duration: '4n',
      velocity: 1
    },
    {
      time: '0:2:0',
      noteName: 'C4',
      duration: '4n',
      velocity: 1
