import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'

export default class Container extends Component {
  constructor(props) {
    super(props)
  }

  handleClick = () => {
    const synthSettings = {
      volume: 10,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'fattriangle',  // "fatsine" | "fatsquare" | "fatsawtooth" | "fattriangle" | "fatcustom" | FatTypeWithPartials | "fmsine" | "fmsquare" | "fmsawtooth" | "fmtriangle" | "fmcustom" | FMTypeWithPartials | "amsine" | "amsquare" | "amsawtooth" | "amtriangle" | "amcustom" | AMTypeWithPartials | TypeWithPartials | OscillatorType | "pulse" | "pwm"
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }
  const synth = new Tone.Synth(synthSettings)



  const channelSettings = {
    volume: 0,
    pan: 0,
    mute: false,
    solo: false
  }
  const channel = new Tone.Channel(channelSettings).toDestination()

  synth.chain(channel)
  synth.triggerAttackRelease('C4', '1')





  // const sequence = [
  //   {
  //     time: '0:0:0',
  //     noteName: 'C3',
  //     duration: '4n',
  //     velocity: 1
  //   },
  //
  // ]
  //
  // // Создаём партию, добавляем в неё секвенцию
  // // и включаем проигрывание
  // const part = new Tone.Part((time, note) => {
  //   synth.triggerAttackRelease(
  //     note.noteName,
  //     note.duration,
  //     time,
  //     note.velocity
  //   )
  // }, sequence).start(0)
  //
  // // Указываем длительность партии
  // part.loopEnd = '2m'
  //
  // // Включаем зацикливание
  // part.loop = true
  //
  // // Включаем звук в браузере
  // // sampler.context.resume()
  //
  // // Включаем отсчёт времени в Tone.js
  // Tone.Transport.start()
  }



  render() {
    return (
      <div className="Container">
        <SC_Button
          text="Art Design & Coding Community"
          handleClick={this.handleClick}
        />
      </div>
    )
  }
}
